const createUserPanelSidebar = () => {
    let userPanel = `<div class="user-panel mt-3 pb-3 mb-3 d-flex"><div class="image"><img src="/AdminLTE-3.0.0-rc.1/dist/img/user2-160x160.jpg" class="img-circle elevation-2" alt="User Image"></div><div class="info"><a href="#" class="d-block" id="panel-user-name"></a></div></div><div class="user-panel mt-2 pb-2 mb-2" id="sidebarLink"></div>`;
    $('#sidebar-user-panel').html(userPanel);
}

const setUserPanelName = () => {
    let user = getUserCredentials();
    $('#panel-user-name').html(user.name);
}

$(createUserPanelSidebar());
$(async () => {
    await sleep(500), $(checkCredentials()), $(setUserPanelName())
});
