const swalDelete = (
    title = "Are you sure?",
    text = "You wont be able to revert this!",
    type = "warning",
    runFunction,
    valFunctionArray = ""
) => {
    Swal.fire({
        title: title,
        text: text,
        type: type,
        showCancelButton: !0,
        confirmButtonColor: "#3085d6",
        cancelButtonColor: "#d33",
        confirmButtonText: "Yes, delete it!"
    }).then(result => {
        if (result.value) {
            window[runFunction](valFunctionArray);
        }
    });
};
const swalConfirm = (
    title = "Order now",
    text = "",
    type = "question",
    runFunction,
    valFunctionArray = ""
) => {
    Swal.fire({
        title: title + "?",
        text: text,
        type: type,
        showCancelButton: !0,
        confirmButtonColor: "#3085d6",
        cancelButtonColor: "#d33",
        confirmButtonText: "Yes, " + title + "!"
    }).then(result => {
        if (result.value) {
            window[runFunction](valFunctionArray);
        }
    });
};
const swalSave = (
    title = "Save?",
    text = "",
    type = "question",
    runFunction,
    ...valFunctionArray
) => {
    Swal.fire({
        title: title,
        text: text,
        type: type,
        showCancelButton: !0,
        confirmButtonColor: "#3085d6",
        cancelButtonColor: "#d33",
        confirmButtonText: "Yes, save it!"
    }).then(result => {
        if (result.value) {
            window[runFunction](valFunctionArray);
        }
    });
};
const swalResponse = (status = "", message = "") => {
    Swal.fire(
        `${status == "success" ? "Success" : "Failed"}`,
        `${message}`,
        `${status}`
    );
};
