toastr.options = {
    closeButton: !1,
    debug: !1,
    newestOnTop: !0,
    progressBar: !1,
    positionClass: "toast-top-right",
    preventDuplicates: !1,
    onclick: null,
    showDuration: "300",
    hideDuration: "1000",
    timeOut: "5000",
    extendedTimeOut: "1000",
    showEasing: "swing",
    hideEasing: "linear",
    showMethod: "fadeIn",
    hideMethod: "fadeOut"
};
const toastSuccess = t => {
    toastr.success(t);
},
    toastInfo = t => {
        toastr.info(t);
    },
    toastWarning = t => {
        toastr.warning(t);
    },
    toastError = t => {
        toastr.error(t);
    },
    toastResponse = (r, t) => {
        if (r == "success") {
            toastSuccess(t);
        } else if (r == "error") {
            toastError(t);
        } else if (r == "info") {
            toastInfo(t);
        } else {
            toastWarning(t);
        }
    };
